import Events from 'events';

class App extends Events.EventEmitter
{

}

class AppProperty extends Events.EventEmitter
{
    constructor(app)
    {
        this.app = app;
    }
}

class AppDictionary extends Observable.Dictionary
{
    constructor(app)
    {
        this.app = app;
    }
}

class AppCollection extends Observable.AppCollection
{
    constructor(app)
    {
        this.app = app;
    }
}

class CoolApp extends App
{
    constructor()
    {
        super(this);

        this.todo = new Todo(this);
    }
}

class Todo extends AppProperty
{

}